public class Pattern1
 {
 public static void pyramidPattern(int n)
 {
 for (int i=0; i<n; i++)
 {
 System.out.print(" ");
 }
 for (int j=0; j<=i; j++ )
 {
 System.out.print("* ");
 }

System.out.println();
 }
 }

public static void main(String args[])
 {
 int n = 5;
 pyramidPattern(n);
 }
 }

public class HollowSquareStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5; 

for (int i = 0; i < size; i++) 
 {
 for (int j = 0; j < size; j++) 
 {
 if (i == 0 || i == size - 1 || j == 0 || j == size - 1) 
 {
 System.out.print("*\ ");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 }
 }

