public class HollowRightTriangleStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5; 

for (int i = 0; i < size; i++) 
 {
 for (int j = 0; j <= i; j++) 
 {
 if (i == 0 || i == j || j == 0) 
 {
 System.out.print("* ");
 } else {
 System.out.print(" \ ");
 }
 }
 System.out.println();
 }
 }
 }

public class LeftTrianglePattern 
 { 
 public static void main(String args[]) 
 { 

int i, j, row = 5; 

for (i=0; i<row; i++) 
 { 

for (j=2*(row-i); j>=0; j-\-) 
 { 

System.out.print("\ "); 
 } 

for (j=0; j<=i; j++ ) 
 { 

System.out.print("* "); 
 } 

System.out.println(); 
 } 
 } 
 } 

public class InvertedRightTriangleStarPattern
 {
 public static void main(String[] args) 
 {
 int size = 5; 

for (int i = size; i >= 1; i-\-)
 {
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("* ");
 }
 System.out.println();
 }
 }
 }



