public class DiamondStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 5; 

// Upper half of the diamond
 for (int i = 0; i < size; i++) 
 {
 for (int j = size - 1; j > i; j-\-) 
 {
 System.out.print(" \ ");
 }
 for (int k = 0; k < 2 * i + 1; k++) 
 {
 System.out.print("* ");
 }
 System.out.println();
 }

// Lower half of the diamond
 for (int i = size - 2; i >= 0; i-\-) 
 {
 for (int j = size - 1; j > i; j-\-) 
 {
 System.out.print(" ");
 }
 for (int k = 0; k < 2 * i + 1; k++) 
 {
 System.out.print("* ");
 }
 System.out.println();
 }
 }
 }

public class HollowDiamondStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 5; 

// Upper half of the diamond
 for (int i = 0; i < size; i++) 
 {
 for (int j = size - 1; j > i; j-\-) 
 {
 System.out.print(" \ ");
 }
 for (int k = 0; k < 2 * i + 1; k++) 
 {
 if (k == 0 || k == 2 * i)
 {
 System.out.print("* ");
 } else {
 System.out.print(" \ ");
 }
 }
 System.out.println();
 }

// Lower half of the diamond
 for (int i = size - 2; i >= 0; i-\-) 
 {
 for (int j = size - 1; j > i; j-\-) 
 {
 System.out.print(" \ ");
 }
 for (int k = 0; k < 2 * i + 1; k++) 
 {
 if (k == 0 || k == 2 * i) {
 System.out.print("* ");
 } else {
 System.out.print(" \ ");
 }
 }
 System.out.println();
 }
 }
 }

