// Lower half of the hourglass
 for (int i = size - 2; i >= 0; i-\-) 
 {
 for (int j = 0; j < i; j++) 
 {
 System.out.print(" \ ");
 }
 for (int k = i; k < size; k++) 
 {
 System.out.print("* ");
 }
 System.out.println();
 }
 }
 }

