public class RhombusStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5; 

// Upper half of the rhombus
 for (int i = 0; i < size; i++) 
 {
 for (int j = size - 1; j > i; j-\-) 
 {
 System.out.print(" ");
 }
 for (int k = 0; k < size; k++) 
 {
 System.out.print("*");
 }
 System.out.println();
 }

// Lower half of the rhombus
 for (int i = 1; i < size; i++) 
 {
 for (int j = 0; j < i; j++) 
 {
 System.out.print(" ");
 }
 for (int k = 0; k < size; k++)
 {
 System.out.print("*");
 }
 System.out.println();
 }
 }
 }

import java.util.Scanner;
 public class HollowRhombusStarPattern 
 {
 public static void main(String[] args) 
 {
 Scanner scanner = new Scanner(System.in);

 System.out.print("Enter the size of the pattern: ");

 int size = scanner.nextInt();

 // Upper half of the pattern

for (int i = 1; i <= size; i++) 

{
 for (int j = 1; j <= size - i; j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= size; j++)
 {
 if (i == 1 || i == size || j == 1 || j == size)
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }

// Lower half of the pattern

for (int i = size - 1; i >= 1; i-\-)

{
 for (int j = 1; j <= size - i; j++) 
 {
 System.out.print(" ");

}

for (int j = 1; j <= size; j++) 

{
 if (i == 1 || i == size || j == 1 || j == size) 

{
 System.out.print("*");

} else {

System.out.print(" ");

}

}
 System.out.println();
 }

scanner.close();
 }
 }

public class PyramidStarPattern
 {
 public static void main(String args[]) 
 {
 int n = 5;

for(int i=0;i<n;i++)
 {
 for(int k=i;k<n;k++)
 System.out.print(" ");
 for(int j=0;j<=i;j++)
 {
 System.out.print("*");
 }

System.out.println();
 }
 }
 }

public class HollowPyramidStarPattern
 {
 public static void main(String args[]) 
 {
 int n = 5; 
 for(int i=0;i<n;i++)
 {
 for(int k=i;k<n;k++)
 System.out.print(" ");

for(int j=0;j<=2*i;j++)
 {

if(j==0 || j==2*i || i==0 || i==n-1)
 System.out.print("*");
 else
 System.out.print(" ");
 }
 System.out.println();
 }
 }
 } 

import java.util.Scanner;

 public class ChessboardStarPattern 
 {
 public static void main(String[] args) 
 {
 Scanner scanner = new Scanner(System.in);

 System.out.print("Enter the size: ");

 int size = scanner.nextInt();

 for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= size; j++) 
 {
 if ((i + j) % 2 == 0) 
 {
 System.out.print("*");

 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }

scanner.close();
 }
 }

import java.util.Scanner;

 public class HollowChessboardStarPattern 
 {
 public static void main(String[] args) 
 {
 Scanner scanner = new Scanner(System.in);

 System.out.print("Enter the size: ");

 int size = scanner.nextInt();

 for (int i = 1; i <= size; i++)
 {
 for (int j = 1; j <= size; j++) 
 {
 if (i == 1 || i == size || j == 1 || j == size || (i + j) % 2 == 0)
 {
 System.out.print("*");

 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }

scanner.close();
 }
 }

public class SwirlStarPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

for (int i = 1; i <= rows; i++) 
 {

for (int j = 1; j < i; j++) 
 {
 System.out.print(" ");

}

for (int k = i; k <= rows; k++) 
 {
 System.out.print("* ");
 }

System.out.println(); 
 }

for (int i = rows - 1; i >= 1; i-\-) 
 {

for (int j = 1; j < i; j++)
 {
 System.out.print(" ");
 }

for (int k = i; k <= rows; k++) 
 {
 System.out.print("* ");
 }

System.out.println(); 
 }
 }
 }

public class HollowSwirlStarPattern
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

for (int i = 1; i <= rows; i++) 
 {

for (int j = 1; j < i; j++) 
 {
 System.out.print(" ");
 }

for (int k = i; k <= rows; k++) 
 {
 if (k == i || k == rows || i == 1) 
 {
 System.out.print("* ");
 } else {
 System.out.print(" \ ");
 }
 }

System.out.println(); 
 }

for (int i = rows - 1; i >= 1; i-\-) 
 {

for (int j = 1; j < i; j++) 
 {
 System.out.print(" ");
 }

for (int k = i; k <= rows; k++) 
 {
 if (k == i || k == rows || i == 1) 
 {
 System.out.print("* ");

 } else {
 System.out.print(" \ ");
 }
 }

System.out.println(); 
 }
 }
 }

public class CircleStarPattern 
 {
 public static void main(String[] args) 
 {
 int radius = 5; 

 int centerX = radius; 

 int centerY = radius; 

 for (int y = 0; y <= 2 * radius; y++) 
 {
 for (int x = 0; x <= 2 * radius; x++)
 {
 double distance = Math.sqrt(Math.pow(x - centerX, 2) + Math.pow(y - centerY, 2));

 if (Math.abs(distance - radius) < 0.5) 
 {
 System.out.print("* ");
 } else {
 System.out.print(" \ ");
 }
 }

System.out.println(); 
 }
 }
 }

public class OvalStarPattern 
 {
 public static void main(String[] args) 
 {
 int width = 5; 

 int height = 3; 

 double aspectRatio = (double) width / height;

for (int y = 0; y <= 2 * height; y++) 
 { for (int x = 0; x <= 2 * width; x++) 
 {
 double normalizedX = (double) (x - width) / width;

 double normalizedY = (double) (y - height) / height;

 if (normalizedX * normalizedX + normalizedY * normalizedY <= 1) 
 {
 System.out.print("* ");
 } else { System.out.print(" \ ");
 }
 }

System.out.println(); 
 }
 }
 }

public class HexagonStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 5;

 int rows = 2 * size - 1;

 for (int i = 1; i <= rows; i++) 
 {
 int spaces = Math.abs(i - size);

 int stars = rows - spaces;

 for (int j = 1; j <= spaces; j++) 
 {
 System.out.print(" ");
 }

for (int k = 1; k <= stars; k++) 
 {
 System.out.print("* ");
 }

System.out.println();
 }
 }
 }

public class HollowHexagonStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5; 
 int rows = 2 * size - 1;

 for (int i = 1; i <= rows; i++) 
 {
 int spaces = Math.abs(i - size);

 int stars = rows - spaces;

 for (int j = 1; j <= spaces; j++)
 {
 System.out.print(" ");
 }

for (int k = 1; k <= stars; k++) 
 {
 if (k == 1 || k == stars || i == 1 || i == rows)
 {
 System.out.print("* ");

 } else {
 System.out.print(" ");
 }
 }

System.out.println();
 }
 }
 }

public class SnowmanStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5;

// Top part of the snowman
 for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= size - i; j++)
 {
 System.out.print(" ");
 }

for (int k = 1; k <= 2 * i - 1; k++) 
 {
 System.out.print("*");
 }

System.out.println();
 }

// Middle part of the snowman
 for (int i = 1; i <= size - 2; i++) 
 {
 for (int j = 1; j <= size - 1; j++) 
 {
 System.out.print(" ");
 }

System.out.println("*");
 }

// Bottom part of the snowman
 for (int i = 1; i <= size; i++)
 {
 for (int j = 1; j <= size - 1; j++) 
 {
 System.out.print(" ");
 }
 System.out.println("*");
 }
 }
 }

public class HollowSnowmanStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5;
 // Top part of the snowman
 for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= size - i; j++)
 {
 System.out.print("\ ");
 }
 for (int k = 1; k <= 2 * i - 1; k++) 
 {
 if (k == 1 || k == 2 * i - 1) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 // Middle part of the snowman
 for (int i = 1; i <= size - 2; i++)
 {
 for (int j = 1; j <= size - 1; j++) 
 {
 System.out.print(" ");
 }
 System.out.print("*");
 System.out.println();
 }
 // Bottom part of the snowman
 for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= size - 1; j++) 
 {
 System.out.print(" ");
 }
 System.out.print("*");
 System.out.println();
 }
 }
 }

public class IceCreamStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 5;

for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= size - i + 1; j++) 
 {
 System.out.print(" ");
 }

for (int k = 1; k <= 2 * i - 1; k++) 
 {
 System.out.print("*");
 }

System.out.println();
 }

for (int i = 1; i <= size - 1; i++) 
 {
 for (int j = 1; j <= size; j++) 
 {
 System.out.print(" ");
 }

for (int k = 1; k <= 2 * i - 1; k++) 
 {
 System.out.print("*");
 }

System.out.println();
 }
 for (int i = 1; i <= size; i++) 
 {
 System.out.print(" ");
 }
 System.out.println("*");
 }
 }

