public class ButterflyStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5; 

for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 int spaces = 2 * (size - i);
 for (int j = 1; j <= spaces; j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 System.out.println();
 }

for (int i = size; i >= 1; i-\-) 
 {
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 int spaces = 2 * (size - i);
 for (int j = 1; j <= spaces; j++)
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++)
 {
 System.out.print("*");
 }
 System.out.println();
 }
 }
 }

public class HollowButterflyStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5;

for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= i; j++) 
 {
 if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 int spaces = 2 * (size - i);
 for (int j = 1; j <= spaces; j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++) 
 {
 if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }

for (int i = size; i >= 1; i-\-) 
 {
 for (int j = 1; j <= i; j++) 
 {
 if (j == 1 || j == i)
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 int spaces = 2 * (size - i);
 for (int j = 1; j <= spaces; j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++) 
 { if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 }
 }

public class DragonStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 5;

for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 for (int j = 1; j <= 2 * (size - i); j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 System.out.println();
 }

for (int i = size - 1; i >= 1; i-\-) 
 {
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 for (int j = 1; j <= 2 * (size - i); j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }
 System.out.println();
 }

for (int i = 1; i <= size; i++) 
 {
 for (int j = size; j >= i; j-\-)
 {
 System.out.print("*");
 }
 for (int j = 1; j <= 2 * (i - 1); j++)
 {
 System.out.print(" ");
 }
 for (int j = size; j >= i; j-\-) 
 {
 System.out.print("*");
 }
 System.out.println();
 }

for (int i = size - 1; i >= 1; i-\-)
 {
 for (int j = size; j >= i; j-\-) 
 {
 System.out.print("*");
 }
 for (int j = 1; j <= 2 * (i - 1); j++) 
 {
 System.out.print(" ");
 }
 for (int j = size; j >= i; j-\-) 
 {
 System.out.print("*");
 }
 System.out.println();
 }
 }
 }

public class HollowDragonStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 5; 

for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= i; j++) 
 {
 if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 for (int j = 1; j <= 2 * (size - i); j++)
 {
 System.out.print(" "); }
 for (int j = 1; j <= i; j++) 
 {
 if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }

for (int i = size - 1; i >= 1; i-\-) 
 {
 for (int j = 1; j <= i; j++)
 {
 if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 for (int j = 1; j <= 2 * (size - i); j++) 
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i; j++) 
 {
 if (j == 1 || j == i) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }

for (int i = 1; i <= size; i++)
 {
 for (int j = size; j >= i; j-\-) 
 {
 System.out.print("*");
 }
 for (int j = 1; j <= 2 * (i - 1); j++)
 {
 System.out.print(" ");
 }
 for (int j = size; j >= i; j-\-) 
 {
 System.out.print("*");
 }
 System.out.println();
 }

for (int i = size - 1; i >= 1; i-\-)
 {
 for (int j = size; j >= i; j-\-) 
 {
 System.out.print("*");
 }
 for (int j = 1; j <= 2 * (i - 1); j++) 
 {
 System.out.print(" ");
 }
 for (int j = size; j >= i; j-\-)
 {
 System.out.print("*");
 }
 System.out.println();
 }
 }
 }

public class RocketStarPattern
 {
 public static void main(String[] args)
 {
 int rows = 5;

for (int i = 1; i <= rows; i++) 
 {n
for (int j = rows - i; j >= 1; j-\-)
 {
 System.out.print(" ");
 }
 for (int k = 1; k <= i; k++) 
 {
 System.out.print("*");
 }
 System.out.println();
 }
 for (int i = 1; i <= rows - 1; i++) 
 {

for (int j = 1; j <= rows - 1; j++) 
 {
 System.out.print(" ");
 }
 System.out.print("*");
 System.out.println();
 }
 for (int i = 1; i <= rows; i++) 
 {
 for (int j = 1; j <= i - 1; j++) 
 {
 System.out.print(" ");
 }
 for (int k = 1; k <= rows - i + 1; k++)
 {
 System.out.print("*");
 }
 System.out.println();
 }
 }
 }

public class HollowRocketStarPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5;

for (int i = 1; i <= rows; i++) 
 {

for (int j = rows - i; j >= 1; j-\-) 
 {
 System.out.print(" ");
 }
 if (i == 1 || i == rows) 
 {
 for (int k = 1; k <= i; k++)
 {
 System.out.print("*");
 }
 } else {
 System.out.print("*");
 for (int k = 1; k <= i - 2; k++) 
 {
 System.out.print(" ");
 }
 System.out.print("*");
 }

System.out.println();
 }

for (int i = 1; i <= rows - 1; i++)
 {

for (int j = 1; j <= rows - 1; j++) 
 {
 System.out.print(" ");
 }
 System.out.print("*");

System.out.println();
 }

for (int i = 1; i <= rows; i++)
 {

for (int j = 1; j <= i - 1; j++) 
 {
 System.out.print(" ");
 }

if (i == 1 || i == rows) 
 {
 for (int k = 1; k <= rows - i + 1; k++) 
 {
 System.out.print("*");
 }
 } else {
 System.out.print("*");
 for (int k = 1; k <= rows - i - 1; k++) 
 {
 System.out.print(" ");
 }
 System.out.print("*");
 }

System.out.println();
 }
 }
 }

public class HeartPattern 
 {
 public static void main(String[] args)
 {
 int rows = 6;

for (int i = rows / 2; i <= rows; i += 2) 
 {
 for (int j = 1; j < rows - i; j += 2) 
 {
 System.out.print(" ");
 }

for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }

for (int j = 1; j <= rows - i; j++) 
 {
 System.out.print(" ");
 }

for (int j = 1; j <= i; j++) 
 {
 System.out.print("*");
 }

System.out.println();
 }

for (int i = rows; i >= 1; i-\-) 
 {
 for (int j = i; j < rows; j++) 
 {
 System.out.print(" ");
 }

for (int j = 1; j <= (i * 2) - 1; j++) 
 {
 System.out.print("*");
 }

System.out.println();
 }
 }
 }

public class InvertedPyramidStarPattern
 {
 public static void main(String args[]) 
 {
 int n = 5; 

for(int i=n;i>0;i-\-)
 {
 for(int k=i;k<n;k++)
 System.out.print(" ");
 for(int j=2*i;j>1;j-\-)
 {
 System.out.print("* ");
 }
 System.out.println();
 }
 }
 } 

public class HollowInvertedPyramidStarPattern
 {
 public static void main(String[] args) 
 {
 int n = 5; 

for (int i = n; i > 0; i-\-) 
 {
 for (int k = i; k < n; k++)
 {
 System.out.print(" ");
 }
 for (int j = 2 * i; j > 1; j-\-) 
 {
 if (i == n || i == 1 || j == 2 * i || j == 2) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 }
 }

