public class ArrowStarPattern
 {
 public static void main(String args[]) 
 {
 int n = 11; 

n = n/2 + 1;

int i=0;

for(i=0;i<n;i++)
 {
 for(int k=i;k<n-1;k++)
 System.out.print(" ");

for(int j=i;j<n;j++)
 {
 System.out.print("* ");
 }

System.out.println();
 }

n=i; 

int m=2;
 for(i=n;i>1;i-\-)
 {
 for(int k=i;k<n+1;k++)
 System.out.print(" ");

for(int j=0;j<m;j++)
 {

System.out.print("* "); }
 m++;
 System.out.println();
 }

}
 } 

