public class CrossStarPattern 
 {
 public static void main(String args[]) 
 {
 int n = 5; 
 int m = 2*n-1; 
 for(int i=1;i<=m;i++) 
 { 
 for(int j=1;j<=m;j++)
 { 

if(i==j || j==(m-i+1))
 { 
 System.out.print("*"); 
 } 
 else 
 System.out.print(" "); 
 }
 System.out.println();
 } 

}
 } 

public class HollowDiamondStarPattern

{
 public static void main(String args[]) 
 { int n = 6; 
 for(int i=1; i<=n; i++)
 {
 for(int j=i; j<=n; j++)
 {
 System.out.print("*");
 }
 for(int j=1; j<=(2*i-1); j++)
 {
 System.out.print(" ");
 }
 for(int j=i; j<=n; j++)
 {
 System.out.print("*");
 }

System.out.println();
 }

for(int i=1; i<=n; i++)
 {
 for(int j=1; j<=i; j++)
 {
 System.out.print("*");
 }
 for(int j=(2*i-2); j<(2*n-1); j++)
 {
 System.out.print(" ");
 }

for(int j=1; j<=i; j++)
 {
 System.out.print("*");
 }

System.out.println();
 }
 }
 }

public class ChristmasTreeStarPattern
 {
 public static void main(String[] args) 
 {
 int height = 4;

int width = 4;

int space = width * height;

int x = 1;

for(int a = 1;a <= height ;a++)
 {
 for(int i = x;i <= width;i++)
 {

for(int j = space;j >= i;j-\-)
 {
 System.out.print(" ");
 }

for(int k = 1;k <= i;k++)
 {
 System.out.print("* ");
 }
 System.out.println();
 }
 x = x+2;
 width = width+2;
 }
 for(int i = 1;i <= height-1;i++)
 {
 for(int j = space-3;j >= 0;j-\-)
 {
 System.out.print(" ");
 }

for(int k= 1;k <= height-1;k++)
 {
 System.out.print("* ");
 }

System.out.println();
 }
 }
 }

public static void main(String[] args) 
 {
 int size = 7; 

for (int i = 1; i <= size; i++) 
 {
 for (int j = 1; j <= size - i; j++)
 {
 System.out.print(" ");
 }
 for (int j = 1; j <= i * 2 - 1; j++) 
 {
 if (j == 1 || j == i * 2 - 1 || i == (size + 1) / 2) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 }
 }

public static void main(String[] args) 
 {
 int size = 7; 

for (int i = 0; i < size; i++) 
 {
 for (int j = 0; j <= size; j++)
 {
 if (j == 0 || (j == size && i != 0 && i != size / 2 && i != size - 1)

 || ((i == 0 || i == size / 2 || i == size - 1) && j < size))

 System.out.print("*");
 else
 System.out.print(" ");
 }
 System.out.println();
 }
 }
 }

