public class QStarPattern 
 {
 public static void main(String[] args) 
 {
 int size = 7; 
 for (int i = 1; i <= size; i++)
 {
 for (int j = 1; j <= size; j++) 
 {
 if ((i == 1 || i == size) && (j > 1 && j < size))
 {
 System.out.print("*");
 } else if ((j == 1 || j == size) && (i > 1 && i < size)) 
 {
 System.out.print("*");
 } else if (i > size / 2 && j > size / 2 && i == j) 
 {
 System.out.print("*");
 } else if (i > size / 2 && j > size / 2 && i + j == size + 1) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 }
 }

