public class ZStarPattern 
 {
 public static void main(String[] args)
 {
 int size = 7; 
 for (int i = 1; i <= size; i++)
 {
 for (int j = 1; j <= size; j++) 
 {
 if (i == 1 || i == size || i + j == size + 1) 
 {
 System.out.print("*");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println();
 }
 }
 }

