public class PrimeNumberPattern 
 {
 public static void main(String[] args)
 {
 int n = 5;
 int[][] matrix = new int[n][n];

int count = 0;
 int num = 2;

while (count < n * n)
 { if (isPrime(num)) 
 { matrix[count / n][count % n] = num;
 count++;
 }
 num++;
 }

for (int i = 0; i < n; i++)
 {
 for (int j = 0; j < n; j++) 
 {
 System.out.print(matrix[i][j] + " ");
 }
 System.out.println();
 }
 }
 public static boolean isPrime(int num) 
 {
 if (num <= 1) 
 {
 return false;
 }

for (int i = 2; i <= Math.sqrt(num); i++)
 {
 if (num % i == 0)
 {
 return false;
 }
 }

return true;
 }
 }

public class FibonacciNumberPattern 
 {
 public static void main(String[] args) 
 {
 int n = 5; 

int a = 0, b = 1;

for (int i = 1; i <= n; i++) 
 {
 for (int j = 1; j <= i; j++) 
 {
 System.out.print(a + " ");
 int sum = a + b;
 a = b;
 b = sum;
 }
 System.out.println(); 
 }
 }
 }

