public class ArmstrongNumberPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

int start = 1;
 int end = 500;
 int count = 0;
 for (int number = start; number <= end; number++)
 {
 int sum = 0;
 int temp = number;
 int numDigits = getNumberOfDigits(temp);

while (temp != 0) 
 {
 int digit = temp % 10;
 sum += Math.pow(digit, numDigits);
 temp /= 10;
 }

if (number == sum) 
 {
 System.out.printf("%-5d", number);
 count++;

if (count % rows == 0)
 {
 System.out.println();
 }
 }
 }
 }
 private static int getNumberOfDigits(int number)
 {
 int count = 0;
 while (number != 0) 
 {
 number /= 10;
 count++;
 }
 return count;
 }
 }

public class PerfectNumberPattern
 {
 public static void main(String[] args)
 {
 int rows = 5; 

int count = 0;
 int number = 1;

while (count < rows) 
 {
 int sum = 0;

for (int i = 1; i < number; i++) 
 {
 if (number % i == 0) 
 {
 sum += i;
 }
 }

if (number == sum)
 {
 System.out.printf("%-5d", number);
 count++;
 }

number++;
 }
 }
 }

public class AbundantNumberPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

int count = 0;
 int number = 12; 
 while (count < rows)
 {
 int sum = getSumOfProperDivisors(number);

if (sum > number)
 { System.out.printf("%-5d", number);
 count++;
 }

number++;
 }
 }
 private static int getSumOfProperDivisors(int number) 
 {
 int sum = 1; 
 for (int i = 2; i * i <= number; i++) 
 {
 if (number % i == 0)
 {
 sum += i;
 if (i != number / i) 
 sum += number / i;
 }
 }
 }

return sum;
 }
 }

public class DeficientNumberPattern
 {
 public static void main(String[] args)
 {
 int rows = 5;

int count = 0;
 int number = 1;

while (count < rows) 
 { int sum = getSumOfProperDivisors(number);

if (sum < number) 
 { System.out.printf("%-5d", number);
 count++;
 }

number++;
 }
 }

private static int getSumOfProperDivisors(int number) 
 {
 int sum = 1; 
 for (int i = 2; i * i <= number; i++) 
 {
 if (number % i == 0) 
 {
 sum += i;
 if (i != number / i) 
 { 
 sum += number / i;
 }
 }
 }

return sum;
 }
 }

public class HarshadNumberPattern
 {
 public static void main(String[] args)
 {
 int rows = 5; 

int count = 0;
 int number = 1;

while (count < rows)
 {
 if (number % getSumOfDigits(number) == 0)
 {
 System.out.printf("%-5d", number);
 count++;
 }

number++;
 }
 }

private static int getSumOfDigits(int number) 
 {
 int sum = 0;

while (number != 0) 
 {
 sum += number % 10;
 number /= 10;
 }

return sum;
 }
 }

public class NeonNumberPattern
 {
 public static void main(String[] args)
 {
 int rows = 5;

int count = 0;
 int number = 0;

while (count < rows)
 {
 if (isNeonNumber(number)) 
 {
 System.out.printf("%-5d", number);
 count++;
 if (count % 5 == 0) 
 {
 System.out.println();
 }
 }

number++;
 }
 }

private static boolean isNeonNumber(int number)
 {
 int square = number * number;
 int sumOfDigits = 0;

while (square != 0) 
 {
 sumOfDigits += square % 10;
 square /= 10;
 }

return sumOfDigits == number;
 }
 }

public class HappyNumberPattern
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

int count = 0;
 int number = 1;

while (count < rows) 
 {
 if (isHappyNumber(number)) 
 {
 System.out.printf("%-5d", number);
 count++;
 if (count % 5 == 0) 
 {
 System.out.println();
 }
 }

number++;
 }
 }

private static boolean isHappyNumber(int number) 
 {
 while (number != 1 && number != 4)
 {
 int sumOfSquares = 0;

while (number != 0) 
 {
 int digit = number % 10;
 sumOfSquares += digit * digit;
 number /= 10;
 }

number = sumOfSquares;
 }

return number == 1;
 }
 }

public class MagicNumberPattern 
 {
 public static void main(String[] args)
 {
 int rows = 5; 
 int count = 0;
 int number = 1;

while (count < rows) 
 {
 if (isMagicNumber(number)) 
 {
 System.out.printf("%-5d", number);
 count++;
 if (count % 5 == 0) 
 {
 System.out.println();
 }
 }

number++;
 }
 }

private static boolean isMagicNumber(int number) 
 {
 int sum = 0;

while (number > 9) 
 {
 while (number != 0) 
 {
 int digit = number % 10;
 sum += digit;
 number /= 10;
 }

number = sum;
 sum = 0;
 }

return number == 1;
 }
 }

public class CatalanNumberPattern
 {
 public static void main(String[] args) 
 {
 int rows = 5; 
 for (int i = 0; i < rows; i++) 
 {
 for (int j = 0; j <= i; j++) 
 {
 System.out.printf("%-5d", calculateCatalanNumber(j));
 }
 System.out.println();
 }
 }

private static int calculateCatalanNumber(int n)
 {
 if (n <= 1) 
 {
 return 1;
 }

int catalan = 0;
 for (int i = 0; i < n; i++) 
 {
 catalan += calculateCatalanNumber(i) * calculateCatalanNumber(n - i - 1);
 }

return catalan;
 }
 }

public class BellNumberPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

for (int i = 0; i < rows; i++) 
 {
 for (int j = 0; j <= i; j++)
 {
 System.out.printf("%-5d", calculateBellNumber(i, j));
 }
 System.out.println();
 }
 }

private static int calculateBellNumber(int n, int k) 
 {
 int[][] bellTriangle = new int[n + 1][n + 1];
 bellTriangle[0][0] = 1;

for (int i = 1; i <= n; i++)
 {
 bellTriangle[i][0] = bellTriangle[i - 1][i - 1];

for (int j = 1; j <= i; j++) 
 {
 bellTriangle[i][j] = bellTriangle[i - 1][j - 1] + bellTriangle[i][j - 1];
 }
 }

return bellTriangle[n][k];
 }
 }

public class TriangularNumberPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

for (int i = 1; i <= rows; i++) 
 {
 for (int j = 1; j <= i; j++)
 {
 int number = getTriangularNumber(i, j);
 System.out.printf("%-5d", number);
 }
 System.out.println();
 }
 }

private static int getTriangularNumber(int row, int column) 
 {
 return (row * (row - 1) / 2) + column;
 }
 }

public class PentagonalNumberPattern 
 {
 public static void main(String[] args)
 {
 int n = 5;

for (int i = 1; i <= n; i++) 
 {
 for (int j = 1; j <= i; j++) 
 {
 int pentagonalNum = getPentagonalNumber(j);
 System.out.print(pentagonalNum + " ");
 }
 System.out.println(); 
 }
 }

public static int getPentagonalNumber(int n) 
 {
 return n * (3 * n - 1) / 2;
 }
 }

public class AmicableNumberPattern 
 {
 public static void main(String[] args) 
 {
 int rows = 5;
 int count = 0;
 int number = 1;
 while (count < rows)
 {
 int sum1 = getSumOfProperDivisors(number);
 int sum2 = getSumOfProperDivisors(sum1);
 if (number != sum1 && number == sum2)
 {
 System.out.printf("%-10d%-10d
", number, sum1);
 count++;
 }
 number++;
 }
 }
 private static int getSumOfProperDivisors(int number) 
 {
 int sum = 1;

for (int i = 2; i <= Math.sqrt(number); i++)
 {
 if (number % i == 0) 
 {
 sum += i;

if (i != number / i) 
 {
 sum += number / i;
 }
 }
 }

return sum;
 }
 }

public class PronicNumberPattern
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

int count = 0;
 int number = 1;

while (count < rows) 
 {
 if (isPronicNumber(number)) 
 {
 System.out.printf("%-10d", number);
 count++;
 if (count % 5 == 0) 
 {
 System.out.println();
 }
 }
 number++;
 }
 }

private static boolean isPronicNumber(int number) 
 {
 int sqrt = (int) Math.sqrt(number);
 return sqrt * (sqrt + 1) == number;
 }
 }

public class LucasNumberPattern
 {
 public static void main(String[] args) 
 {
 int rows = 5; 

int count = 0;
 int n1 = 2; 
 int n2 = 1; 

while (count < rows) 
 {
 System.out.printf("%-10d", n1);
 count++;
 if (count % 5 == 0) 
 {
 System.out.println();
 }

int nextLucas = n1 + n2;
 n1 = n2;
 n2 = nextLucas;
 }
 }
 }

