public class SquareAlphabetPattern 
 {
 public static void main(String[] args) 
 {
 int size = 5; 
 char startChar = 'A'; 

for (int i = 0; i < size; i++) 
 {
 for (int j = 0; j < size; j++)
 {
 char ch = (char) (startChar + j + i);
 System.out.print(ch + " ");
 }
 System.out.println(); 
 }
 }
 }

public class HollowSquareAlphabetPattern
 {
 public static void main(String[] args) 
 {
 int size = 5; 
 char startChar = 'A'; 

for (int i = 0; i < size; i++)
 {
 for (int j = 0; j < size; j++) 
 {
 if (i == 0 || i == size - 1 || j == 0 || j == size - 1) 
 {
 char ch = (char) (startChar + Math.abs(j - i));
 System.out.print(ch + " ");
 } else {
 System.out.print(" ");
 }
 }
 System.out.println(); 
 }
 }
 }

