public class LeftTriangle
 {
 public static void main(String[] args) 
 { int rows = 5; 
 char Char = 'A'; 
 for (int i = 0; i < rows; i++) 
 {

for (int j = 0; j < rows - i - 1; j++) 
 {
 System.out.print(" ");
 }

for (int k = 0; k <= i; k++) 
 {
 char ch = (char) (Char + k);
 System.out.print(ch + " ");
 }

System.out.println(); 
 }
 }
 }

