public class LeftHalfAlphabet
 {
 public static void main(String[] args)
 {
 int n = 5;
 char currentChar = 'A';
 for (int i = 1; i <= n; i++) 
 {
 for (int j = 1; j <= n - i; j++) 
 {
 System.out.print(" ");
 }
 for (int k = 1; k <= i; k++) 
 {
 System.out.print(currentChar);
 currentChar++;
 }
 System.out.println();
 }
 currentChar-\-;
 for (int i = n - 1; i >= 1; i-\-) 
 {
 for (int j = 1; j <= n - i; j++) 
 {
 System.out.print(" ");
 }
 for (int k = 1; k <= i; k++)
 {
 System.out.print(currentChar);
 currentChar++;
 }
 currentChar -= 2;
 System.out.println();
 }
 }
 }

