public class AlphabeticPattern
 {
 public static void main(String[] args) 
 {
 int n = 6; 
 char currentChar = 'A';

for (int i = 1; i <= n; i++)
 {

for (int j = 1; j <= i; j++) 
 {
 System.out.print(currentChar);
 currentChar++;
 }

for (int j = 1; j <= 2 * (n - i); j++)
 {
 System.out.print(" ");
 }

for (int j = 1; j <= i; j++)
 {
 currentChar-\-; System.out.print(currentChar);
 }

System.out.println();
 }
 for (int i = n - 1; i >= 1; i-\-)
 {

for (int j = 1; j <= i; j++)
 {
 System.out.print(currentChar);
 currentChar++;
 }
 for (int j = 1; j <= 2 * (n - i); j++) 
 {
 System.out.print(" ");
 }

for (int j = 1; j <= i; j++)
 {
 currentChar-\-;
 System.out.print(currentChar);
 }

System.out.println();
 }
 }
 }

