// Leap year program in Java
 // If the year satisfies either of the conditions, it's considered a leap year 
 // 1. The year must be divisible by 400.
 // 2. The year must be divisible by 4 but not 100.


public class Main
 {
 public static void main (String[]args)
 {

int year = 2020;

if (year % 400 == 0)
 System.out.println (year + " is a Leap Year");

else if (year % 4 == 0 && year % 100 != 0)
 System.out.println (year + " is a Leap Year");

else
 System.out.println (year + " is not a Leap Year");

}
 }

public class Main
 {
 public static void main (String[]args)
 {
 int n = 23; checkPrime (n);
 }

private static void checkPrime (int n)
 {
 int count = 0;

// negative numbers, 0 and 1 are not prime
 if (n < 2)
 {
 System.out.println ("The given is number " + n + " is not prime");
 System.exit (0);
 }
 // checking the number of divisors b/w [1, n]
 for (int i = 1; i <= n; i++)
 {
 if (n % i == 0)
 count += 1;
 }

// if count of divisors greater than 2 then its not prime 
 if (count > 2)
 System.out.println ("The given is number " + n + " is not prime");

else
 System.out.println ("The given is number " + n + " is prime");
 }
 }

public class Main
 {
 public static void main (String[]args)
 {

int lower = 1, upper = 20;

for (int i = lower; i <= upper; i++)
 if (isPrime (i))
 System.out.println (i);
 }

static boolean isPrime (int n)
 {
 int count = 0;

// 0, 1 negative numbers are not prime
 if (n < 2)
 return false;

// checking the number of divisors b/w 1 and the number n-1
 for (int i = 2; i < n; i++)
 {
 if (n % i == 0)
 return false;
 }

// if reached here then must be true
 return true;
 }
 }

