 num = num / 10;
 }

//output
 System.out.println ("Sum of digits : " + sum);
 }

}

public class Main
 {
 public static void main (String[]args)
 {

//variables initialization
 int num = 1234, reverse = 0, rem;

//loop to find reverse number
 while (num != 0)
 {
 rem = num % 10;
 reverse = reverse * 10 + rem;
 num /= 10;
 };

//output
 System.out.println ("Reversed Number: " + reverse);
 }
 }

public class Main
 {
 public static void main (String[]args)
 {
 //variables initialization
 int num = 12021, reverse = 0, rem, temp;

temp = num;
 //loop to find reverse number
 while (temp != 0)
 {
 rem = temp % 10;
 reverse = reverse * 10 + rem;
 temp /= 10;
 };

// palindrome if num and reverse are equal
 if (num == reverse)
 System.out.println (num + " is Palindrome");
 else
 System.out.println (num + " is not Palindrome");
 }
 } 

public class Main
 {
 public static void main (String[]args)
 {
 int num = 407, len;

// function to get order(length)
 len = order (num);

// check if Armstrong
 if (armstrong (num, len))
 System.out.println(num + " is armstrong");
 else
 System.out.println(num + " is armstrong");

}

static int order (int x)
 {
 int len = 0;
 while (x != 0 )
 {
 len++;
 x = x / 10;
 }
 return len;
 }

static boolean armstrong (int num, int len)
 {

int sum = 0, temp, digit;
 temp = num;

// loop to extract digit, find power & add to sum
 while (temp != 0)
 {
 // extract digit
 digit = temp % 10;

// add power to sum
 sum = sum + (int)Math.pow(digit, len);
 temp /= 10;
 };

return num == sum;
 }
 }

//Java program to print armstrong numbers between two intervals

 import java.util.Scanner;

public class Main
 {
 public static void main(String[] args)
 {
 Scanner sc = new Scanner(System.in);
 System.out.print("Enter lower and upper ranges : ");
 int low = sc.nextInt();
 int high = sc.nextInt();

System.out.println("Armstrong numbers between " + low + " and " + high + " are : ");

// loop for finding and printing all armstrong numbers between given range
 for(int num = low ; num <= high ; num++)
 {
 int sum = 0, temp, digit, len;

len = getOrder(num);
 temp = num;
 // loop to extract digit, find ordered power of digits & add to sum
 while(temp != 0)
 {
 // extract digit
 digit = temp % 10;

// add power to sum
 sum = sum + (int) Math.pow(digit,len);
 temp /= 10;
 };

if(sum == num)
 System.out.print(num + " ");
 }

}

private static int getOrder(int num)
 {
 int len = 0;
 while (num!=0)
 {
 len++;
 num = num/10;
 }
 return len;
 }
 }

public class Main
 {
 public static void main (String[]args)
 {

int num = 15;
 int a = 0, b = 1;

// Here we are printing 0th and 1st terms
 System.out.print (a + " , " + b + " , ");

int nextTerm;

// printing the rest of the terms here
 for (int i = 2; i < num; i++)
 {
 nextTerm = a + b;
 a = b;
 b = nextTerm;
 System.out.print (nextTerm + " , ");
 }

}
 } 

class Main
 {

static int factorial(int n)
 {
 int res = 1, i; for (i = 2; i <= n; i++)
 res *= i;
 return res;
 }

public static void main(String[] args)
 {
 int num = 6;
 System.out.println("Factorial of " + num + " is " + factorial(num)); }
 }

