public class Main
 {
 public static void main (String[]args)
 {

int num = 145;

if (detectStrong (num))
 System.out.println (num + " is Strong Number");
 else
 System.out.println (num + " is not a Strong Number");
 }

static int facto (int n)
 {
 int fact = 1;

for (int i = 1; i <= n; i++)
 fact = fact * i;

return fact;
 }

static boolean detectStrong (int num)
 {

int digit, sum = 0;
 int temp = num;
 boolean flag = false;

// calculate 1! + 4! + 5!
 while (temp != 0)
 {
 digit = temp % 10;

sum = sum + facto (digit);
 temp /= 10;
 }

if (sum == num)
 flag = true;
 else
 flag = false;

return flag;

}
 }

public class Main
 {
 public static void main (String[]args)
 {

int n = 28, sum = 0;

for (int i = 1; i < n; i++)
 {
 if (n % i == 0)
 sum = sum + i;
 }

if (sum == n)
 System.out.println (n + " Is a perfect number");
 else
 System.out.println (n + " Is not a perfect number");

}
 }

public class Main
 {
 public static void main(String[] args) 
 {

int n = 376, sq = n * n ;
 if(isAutomorphic(n) == 1)
 System.out.println("Num: " + n + ", Square: " + sq + " - is Automorphic");
 else
 System.out.println("Num: " + n + ", Square: " + sq + " - is not Automorphic");

}

static int isAutomorphic(int n)
 {
 int square = n * n;
 while(n != 0)
 {
 // means not automorphic number
 if(n % 10 != square % 10)
 {
 return 0;
 }
 // reduce down numbers
 n /= 10;
 square /= 10;
 }
 // if reaches here means automorphic number
 return 1;
 }
 }

public class Main 
 {
 public static void main(String[] args)
 {

int n = 47;

int result = 0;

while(n != 0)
 {
 int pick_last = n % 10;
 result = result + pick_last;
 n = n / 10;
 }

if(n % result == 0)
 System.out.println("Harshad Number");
 else
 System.out.println("Not a Harshad Number");
 }
 }

public class Main
 {
 public static void main (String[]args)
 {

int n = 12, sum = 0;

for (int i = 1; i < n; i++) 
 { 
 if (n % i == 0) sum = sum + i; 
 } 
 if (sum > n)
 {
 System.out.println (n + " is an Abundant Number");
 System.out.println ("The Abundance is: " + (sum - n));
 }
 else
 System.out.println (n + " is not an Abundant Number");
 }
 }

public class Main
 {
 public static void main (String[]args)
 {

int num1 = 30, num2 = 140;

int sum1 = getDivisorsSum (num1);
 int sum2 = getDivisorsSum (num2);

if (sum1 / num1 == sum2 / num2)
 System.out.println (num1 + " & " + num2 + " are friendly pairs");
 else
 System.out.println (num1 + " & " + num2 + " are not friendly pairs");
 }

static int getDivisorsSum (int num)
 {

int sum = 0;

for (int i = 1; i < num; i++)
 {
 if (num % i == 0)
 sum = sum + i;
 }
 return sum;
 }
 }

