public class Main
 {

public static void main (String[] args) 
 {

int x=-3, y=-33;

//find true condition of first quadrant
 if(x>0 && y>0)
 System.out.println("Quadrant I"); 

//find second quadrant
 else if(x<0 && y>0)
 System.out.println("Quadrant II");

//To find third quadrant
 else if(x<0 && y<0) System.out.println("Quadrant III"); 

//To find Fourth quadrant 
 else if (x > 0 && y < 0)
 System.out.println("Quadrant IV");

//To find dose not lie on origin
 else if (x == 0 && y == 0)
 System.out.println("Origin");

//On x-axis
 else if (y==0 && x!=0)
 System.out.println("x-axis");

//On y-axis
 else if (x==0 && y!=0)
 System.out.println("y-axis");

}
 }

import java.util.*; 

public class Main
 { public static void main(String[] args) 
 { 

int n, r, per, fact1, fact2; 

Scanner sc = new Scanner(System.in); 

System.out.println("Enter the Value of n: "); 
 n = sc.nextInt();

System.out.println("Enter the Value of r: ");
 r = sc.nextInt(); 

fact1 = 1; 

for (int i = n; i > 1; i=i-1) 
 { 
 //calculating factorial (n!)
 fact1 = fact1 * i; 
 } 

int number; 

number = n - r; 

fact2 = 1; 

for (int i = number; i > 1; i=i-1) 
 { 
 //calculating factorial ((n-r)!)
 fact2 = fact2 * i;
 } 

//calculating nPr
 per = fact1 / fact2; 

System.out.println(per+ " ways"); 
 } 
 }

