import java.util.Scanner;
 public class Main
 {
 public static void main(String[] args)
 {

Scanner sc = new Scanner(System.in);

System.out.print("Enter numerator for first fraction : ");
 int num1 = sc.nextInt();
 System.out.print("Enter denominator for first fraction : ");
 int den1 = sc.nextInt();
 System.out.print("Enter numerator for second fraction : ");
 int num2 = sc.nextInt();
 System.out.print("Enter denominator for second fraction : "); 
 int den2 = sc.nextInt();
 int num, den, x;
 System.out.print("("+num1+" / "\+den1+") + ("+num2+"\ / "+den2+") = ");
 //logic for calculating sum of two fractions
 if(den1 == den2)
 {
 num = num1 + num2 ;
 den = den1 ;
 }
 else{
 num = (num1*den2) + (num2*den1);
 den = den1 * den2;
 }
 if(num > den)
 x = num;
 else
 x = den;
 for(int i = 1 ; i <= x ; i++)
 {
 if(num%i == 0 && den%i == 0)
 {
 num = num/i;
 den = den/i;
 }
 }
 //logic for getting simplified fraction
 int n = 1;
 int p = num;
 int q = den;
 if( num != den)
 {
 while(n != 0)
 {
 //storing remainder
 n = num % den; 
 if(n != 0)
 { num = den;
 den = n;
 }
 } 
 }
 System.out.println("("\+p/den+" / "+q/den+")");

sc.close(); 
 }
 } 

import java.util.Scanner;
 public class Main
 {
 public static void main(String[] args)
 {

Scanner sc = new Scanner(System.in);
 //input from user
 System.out.print("Enter a number : "); int number = sc.nextInt();
 int x = 0;
 for(int i = 2 ; i <= number/2 ; i++)
 {
 if(prime_or_not(i) == 1)
 {
 if(prime_or_not(number-i) == 1)
 {
 System.out.println(number+ " = " +i+ " + "+(number-i));
 x = 1;
 }
 }
 }
 if(x == 0)
 System.out.println(+number+" cannot be expressed as a sum of two prime numbers");
 }
 //function for checking number is prime or not
 public static int prime_or_not(int n)
 {
 int c = 1;
 for(int i = 2 ; i < n ; i++)
 {
 if(n % i == 0)
 {
 c = 0;
 break;
 }
 }
 return c;
 }
 }

import java.io.*;

public class Main
 {
 public static int cnt_decoding_digits(char[] dig, int a)
 {
 int cnt[] = new int[a+ 1];

cnt[0] = 1;

cnt[1] = 1;

for (int k = 2; k <= a; k++) 
 { 
 cnt[k] = 0; 

cnt[k] = cnt[k-1];

if (dig[k-2] == '1' || (dig[k-2] == '2' && dig[k-1] < '7') )

cnt[k] += cnt[k-2];
 }
 return cnt[a];
 }
 public static void main (String[] args)
 {
 String s="123";

char[] dig= s.toCharArray();

System.out.println(cnt_decoding_digits(dig, 3));
 }
 }

