 public static boolean checkPrime(int num)
 {
if(num<2)
 {
 return false;
 }
 else
 {

int x= num/2;
 for(int i=2;i<x;i++)

{
 if(num%i==0)
 {
 return false;
 }
 }
 }

return true;
 }
 }

