import java.io.*;
 import static java.lang.Math.*;
 class Main
 {

static void findRoots(int a, int b, int c)
 {
 if (a == 0)
 {
 System.out.println("Invalid"); return;
 }

int d = b * b - 4 * a * c;
 double sqrt_val = sqrt(abs(d));

if (d > 0)
 { System.out.println("Roots are real and different");
 System.out.println((double)(-b + sqrt_val) / (2 * a) + "
"+ (double)(-b - sqrt_val) / (2 * a));
 }
 else if (d == 0) 
 {
 System.out.println("Roots are real and same "); System.out.println(-(double)b / (2 * a) + "
" + -(double)b / (2 * a));
 }
 else // d < 0
 {
 System.out.println("Roots are complex");

System.out.println(-(double)b / (2 * a) + " + i" + sqrt_val + "
" + -(double)b / (2 * a) + " - i" + sqrt_val);
 }
 }

public static void main(String args[])
 {

int a = 1, b = 4, c = 4;

findRoots(a, b, c);
 }
 }

class Main
 {
 public static void main(String[] args) 
 {
 int base = 5, x = 3;
 System.out.println("Required Power is " + power(base, x));
 }
 static int power(int base, int x)
 {
 int result = 1;
 while (x > 0) 
 {
 result *= base;
 x-\-;
 }
 return result;
 }
 }

public class Main
 {
 static boolean isPrime(int n)
 {
 // Check if number is less than
 // equal to 1
 if (n <= 1)
 return false;
 // Check if number is 2
 else if (n == 2)
 return true;

// Check if n is a multiple of 2
 else if (n % 2 == 0)
 return false;

// If not, then just check the odds
 for (int i = 3; i <= Math.sqrt(n); i += 2)
 {
 if (n % i == 0)
 return false;
 }
 return true;
 }

public static void main(String[] args)
 {
 if (isPrime(20))
 System.out.println("true");

else
 System.out.println("false");
 }
 }

