public class Main
 {

static void printPermutn(String str, String ans) 
 {
 if (str.length() == 0) 
 {
 System.out.print(ans + " ");
 return;
 }
 for (int i = 0; i < str.length(); i++)
 { char ch = str.charAt(i);
 String r = str.substring(0, i) + str.substring(i + 1);
 printPermutn(r, ans + ch);
 }
 }
 public static void main(String[] args) 
 {
 String s = "abb";
 printPermutn(s, " ");
 }
 }

class Main
 {
 public static void main (String args[])
 {
 int n = 3;
 System.out.println (term (1, 1, n));
 }
 static int term (int calculated, int current, int N)
 {
 int i, cur = 1;
 if (current == N + 1)
 return 0;
 for (i = calculated; i < calculated + current; i++)
 cur *= i;
 return cur + term (i, current + 1, N);
 }
 }

class Main 
 {

static void subsetSums(int[] arr, int l, int r, int sum)
 {

if (l > r)
 {
 System.out.print(sum + " ");
 return;
 }

subsetSums(arr, l + 1, r, sum + arr[l]);

subsetSums(arr, l + 1, r, sum);
 }

public static void main(String[] args)
 {
 int[] arr = { 5, 4, 3 };
 int n = arr.length;

subsetSums(arr, 0, n - 1, 0);
 }
 }

