import java.util.ArrayList;
 import java.util.Deque;
 import java.util.LinkedList;

public class Main
 {
 public static void main(String[] args)
 {
 String input = "nitin";
 System.out.println("All possible palindrome partitions for " + input+ " are : ");
 allPalPartitions(input);
 }

private static void allPalPartitions(String input)
 {
 int n = input.length();

ArrayList<ArrayList> allPart = new ArrayList<>();

Deque currPart = new LinkedList();

allPalPartitonsUtil(allPart, currPart, 0, n, input);

for (int i = 0; i < allPart.size(); i++)
 { for (int j = 0; j < allPart.get(i).size(); j++)
 {
 System.out.print(allPart.get(i).get(j) + " ");
 }
 System.out.println();
 }

}

private static void allPalPartitonsUtil(ArrayList<ArrayList> allPart,
 Deque currPart, int start, int n, String input)
 {
 if (start >= n)
 {
 allPart.add(new ArrayList<>(currPart));
 return;
 }

for (int i = start; i < n; i++)
 {
 if (isPalindrome(input, start, i))
 {
 currPart.addLast(input.substring(start, i + 1));
 allPalPartitonsUtil(allPart, currPart, i + 1, n, input);
 currPart.removeLast();
 }
 }
 }

private static boolean isPalindrome(String input,int start, int i)
 {
 while (start < i)
 {
 if (input.charAt(start++) != input.charAt(i-\-))
 return false;
 }
 return true;
 }
 }

class Main 
 {
 static void printRec(String number, int extraOnes, int remainingPlaces)
 {
 if (0 == remainingPlaces)
 {
 System.out.print(number + " ");
 return;
 }

printRec(number + "1", extraOnes + 1, remainingPlaces - 1);

if (0 < extraOnes)
 printRec(number + "0", extraOnes - 1, remainingPlaces - 1);
 }

static void printNums(int n)
 {
 String str = " ";
 printRec(str, 0, n);
 }

public static void main(String[] args)
 {
 int n = 4;

printNums(n);
 }
 }

import java.util.*;

public class Main
 {

public static String removeAdjacentDuplicates(String str) 
 {
 if (str.length() <= 1) 
 {
 return str;
 }

char firstChar = str.charAt(0);
 String remainingStr = removeAdjacentDuplicates(str.substring(1));

if (firstChar == remainingStr.charAt(0)) 
 {
 return remainingStr.substring(1);
 } else {
 return firstChar + remainingStr;
 }
 }

public static void main(String[] args) 
 {
 String str = "azxxzy";
 String result = removeAdjacentDuplicates(str);
 System.out.println(result);
 }
 }

