class Main
 {
 public static void main (String[] args) 
 {
 int[] arr = {5, 8, 5, 7, 8, 10};
 int size = arr.length;
 countFrequency(arr, size);
 }

static void countFrequency(int[] array, int size)
 {

for (int i = 0; i < size; i++)
 { int flag = 0;
 int count = 0;

for (int j = i+1; j < size; j++)
 {
 if (array[i] == array[j])
 {
 flag = 1;
 break;
 }
 }

if (flag == 1)
 continue;

for (int j = 0;j<=i;j++)
 {
 if (array[i] == array[j])
 count++;
 }
 System.out.println(array[i]+": "+count);
 }
 }
 }

import java.util.*;
 public class Main 
 {
 static Integer[] arr = {10, 20, 10, 10, 20, 30, 30, 30, 30, 0};

public static void sortBasedOnFrequencyAndValue(List<Integer> list)
 {
 int n = arr.length;
 final HashMap<Integer, Integer> mapCount = new HashMap<Integer, Integer>();
 final HashMap<Integer, Integer> mapIndex = new HashMap<Integer, Integer>();

for (int i = 0; i < n; i++) 
 {
 if (mapCount.containsKey(arr[i])) 
 {
 mapCount.put(arr[i],mapCount.get(arr[i]) + 1);
 }
 else 
 {
 mapCount.put(arr[i],1);
 mapIndex.put(arr[i],i);
 }
 }
 Collections.sort(list, new Comparator<Integer>()
 {
 public int compare(Integer n1, Integer n2)
 {
 int freq1 = mapCount.get(n1);
 int freq2 = mapCount.get(n2);
 if (freq1 != freq2)
 {
 return freq2 - freq1;
 }
 else 
 {
 return mapIndex.get(n1) - mapIndex.get(n2);
 }
 }
 });
 System.out.println(list);
 }

public static void main(String[] args) 
 {
 List<Integer> list = Arrays.asList(arr);
 sortBasedOnFrequencyAndValue(list);
 }
 }

import java.util.*;

class Main
 {

static boolean isPalindrome(int n)
 {

int divisor = 1;
 while (n / divisor >= 10)
 divisor *= 10;

while (n != 0) 
 {
 int x = n / divisor;
 int y = n % 10;

if (x != y)
 return false;

n = (n % divisor) / 10;

divisor = divisor / 100;
 }
 return true;
 }

static int largestPalindrome(int []A, int n)
 {
 int res = -1;

for (int i = 0; i < n; i++) 
 { 
 if (A[i] > res && isPalindrome(A[i]))
 res = A[i];
 }

return res;
 }

public static void main(String []args)
 {
 int []A = { 121, 2322, 54545, 999990 };
 int n = A.length;

System.out.println(largestPalindrome(A, n));
 }

}

import java.util.Arrays;

class Main
 {
 public static void countFreq(int arr[], int n)
 {
 boolean visited[] = new boolean[n];
 Arrays.fill(visited, false);
 int count_dis=0;

for (int i = 0; i < n; i++) 
 {

if (visited[i] == true)
 continue;

for (int j = i + 1; j < n; j++)
 {
 if (arr[i] == arr[j])
 {
 visited[j] = true;

}
 }
 count_dis = count_dis+1;
 }
 System.out.println(count_dis);
 }

public static void main(String []args)
 {
 int arr[] = new int[]{10, 30, 40, 20, 10, 20, 50, 10};
 int n = arr.length;
 countFreq(arr, n);
 }
 }

