class Main 
 {

static int maxSubarrayProduct(int arr[])
 {

int result = arr[0];
 int n = arr.length;

for (int i = 0; i < n; i++)
 {
 int mul = arr[i];

for (int j = i + 1; j < n; j++)
 {

result = Math.max(result, mul);
 mul *= arr[j];
 }

result = Math.max(result, mul);
 }
 return result;
 }

public static void main(String[] args)
 {
 int arr[] = { 10, -20, -30, 0, 70, -80, -20 };
 System.out.println("Maximum Sub array product is " + maxSubarrayProduct(arr));
 }
 } 

class Main
 {

static boolean Disjoint(int arr1[], int arr2[])
 {

for(int i=0; i<arr1.length; i++)
 {
 for(int j=0; j<arr2.length; j++)
 {
 if(arr1[i]==arr2[j])
 {
 return false; 
 }
 }
 }
 return true;
 }

public static void main (String[] args)
 {
 int arr1[] = {10, 51, 3, 43, 6};
 int arr2[] = {80, 71, 29, 3};

if (Disjoint(arr1, arr2))
 System.out.println("Yes");
 else
 System.out.println("No");
 }
 }

class Main
 {

static boolean isSubset(int arr1[], int arr2[], int m, int n)
 {
 int i = 0;
 int j = 0;
 for (i = 0; i < n; i++)
 {
 for (j = 0; j < m; j++)
 {
 if (arr2[i] == arr1[j])
 break;
 }

if (j == m)
 return false;
 }

return true;
 }

public static void main(String args[])
 {
 int arr1[] = { 11, 10, 13, 21, 30, 70 };
 int arr2[] = { 11, 30, 70, 10 };

int m = arr1.length;
 int n = arr2.length;

if (isSubset(arr1, arr2, m, n))
 System.out.print("arr2[] is subset of arr1[] "); else
 System.out.print("arr2[] is not subset of arr1[] ");

}
 }

import java.util.*;
 public class Main
 {
 public static boolean make_equal(int arr[], int n)
 {
 for (int i = 0; i < n; i++)
 {

while (arr[i] % 2 == 0)
 arr[i] /= 2;

while (arr[i] % 3 == 0)
 arr[i] /= 3;
 }

for (int i = 1; i < n; i++)
 if (arr[i] != arr[0])
 {
 return false;
 }
 return true;
 }

public static void main (String[] args)
 {
 int arr[] = { 50, 100, 75 };

int m = arr.length;

if (make_equal(arr, m))
 System.out.print("Yes");
 else
 System.out.print("No");
 }

}

import java.util.Arrays;

public class Main
 {

static int sumOfMinAbsDifferences(int arr[] ,int n)
 {

int result = Integer.MAX_VALUE;

for (int i = 0; i < n; i++)
 {
 int sum =0;
 for(int j=0; j<n; j++)
 {
 sum += Math.abs(arr[i] - arr[j]);
 }

result = Math.min(sum, result);
 }

return result;
 } 

public static void main(String args[])
 {
 int arr[] = {2, 4, 5, 3};
 int n = arr.length;

System.out.println( "Required Minimum Sum is " + sumOfMinAbsDifferences(arr, n));
 }
 } 

import java.util.*;

class Main
 {

static int first(int arr[], int l, int h, int x, int n)
 {
 if (h >= l)
 {
 int mid = l + (h-l)/2;

if ((mid == 0 || x > array[mid-1]) && array[m] == x)
 return mid;
 if (x > array[m])
 return first(arr, (m + 1), h, x, n);
 return first(array, l, (m -1), x, n);
 }
 return -1;
 }

static void sort_according(int array1[], int array2[], int m, int n)
 {

int temp[] = new int[m1], visited[] = new int[m1];
 for (int i = 0; i < m1; i++)
 {
 temp[i] = array1[i];
 visited[i] = 0;
 }

Arrays.sort(temp);
 int ind = 0;

for (int i = 0; i < n; i++)
 {

int f = first(temp, 0, m1-1, array2[i], m);

if (f == -1) continue;

for (int j = f; (j < m && temp[j] == arr2[i]); j++)
 {
 arr1[ind++] = temp[j];
 visited[j] = 1;
 }
 }

for (int i = 0; i < m1; i++)
 if (visited[i] == 0)
 arr1[ind++] = temp[i];
 }

static void print_array(int array[], int n)
 {
 for (int i = 0; i < n; i++)
 System.out.print(array[i] + " ");
 System.out.println();
 }

public static void main(String args[])
 {
 int arr1[] = {1, 2, 3, 4, 3, 2, 4, 2, 5};
 int arr2[] = {4, 2, 1, 3};
 int m = arr1.l;
 int n = arr2.l;
 System.out.print("The Sorted array : ");
 sort_according(array1, array2, m1, n);
 print_array(arr1, m1);
 }
 } 

