 }
 }

import java.util.*;

public class Main
 {
 static int equilibrium_index(int arr[], int n)
 {
 int sum = 0;
 int leftsum = 0;

for (int i = 0; i < n; ++i)
 sum += array[i];

for (int i = 0; i < n; ++i) 
 {
 sum -= array[i];

if (leftsum == sum)
 return i;

leftsum += array[i];
 }

return -1;
 }

public static void main(String[] args)
 {
 int arr[] = { 1,2,3,4,5,1,3,2,4 };
 int arr_size = arr.length;
 System.out.print("Equilibrium Index : ");
 System.out.println(equilibrium_index(arr, arr_size));
 }
 } 

class Main 
 {

public static void main(String[] args)
 {
 int arr[] = { 10, 20, 30, 40, 50, 60, 70};
 int n = arr.length;
 int k = 3;

int[] temp;

temp = new int[n];

for(int i=0; i<k; i++)
 temp[i] = arr[i];

int x = k;
 for(int i=0; x < n; i++)
 {
 arr[i] = arr[x++];
 }

x = 0;

for(int i=n-k; i<n; i++)
 arr[i] = temp[x++];

for (int i = 0; i < 7; i++)
 System.out.print(arr[i] + " ");
 }
 }

import java.util.*;

class Main
 {

public static void leftRotate(int arr[], int d, int n)
 {
 leftRotateRec(arr, 0, d, n);
 }

public static void leftRotateRec(int arr[], int i, int d, int n)
 {

if(d == 0 || d == n)
 return;

if(n - d == d)
 {
 swap(arr, i, n - d + i, d);
 return;
 }

if(d < n - d)
 {
 swap(arr, i, n - d + i, d);
 leftRotateRec(arr, i, d, n - d); 
 }
 else{
 swap(arr, i, d, n - d);
 leftRotateRec(arr, n - d + i, 2 * d - n, d);
 }
 }

public static void swap(int arr[], int fi, int si, int d)
 {
 int i, temp;

for(i = 0; i < d; i++)
 {
 temp = arr[fi + i];
 arr[fi + i] = arr[si + i];
 arr[si + i] = temp;
 }
 }

public static void main (String[] args)
 {
 int arr[] = {10, 20, 30, 40, 50, 60, 70};
 leftRotate(arr, 2, 7);

for( int i = 0; i < 7; i++)
 System.out.print(arr[i] + " ");
 }
 } 

public class Main
 {
 public static int hcf(int a, int c) 
 { 
 if (c == 0) 
 return a; 
 else
 return hcf(c, a % b); 
 }

public static void leftRotate(int arr[], int d, int n) 
 { 
 int i, l, m, temp; 
 for (i = 0; i < hcf(d, n); i++) 
 { 

temp = arr[i]; 
 l = i; 
 while (true) 
 { 
 m = l + d; 
 if (m >= n) 
 m = m - n; 
 if (m == i) 
 break; 
 arr[l] = arr[m]; 
 l = m; 
 } 
 arr[l] = temp; 
 }
 } 

public static void main(String[] args) 
 { 
 int arr[] = { 10, 20 30, 40, 50, 60, 70 }; 
 int no_of_rotation = 3;
 int n = arr.length;
 System.out.println(\ "Array Elements before rotating : "); 
 for(int i = 0 ; i < n ; i++)
 {
 System.out.print(arr[i]+ " "); 
 }
 leftRotate(arr, no_of_rotations, n); 
 System.out.println( "Array Elements after rotating : "); 
 for(int i = 0 ; i < n ; i++)
 {
 System.out.print(arr[i] + " "); 

}
 }
 } 

class Main
 {

static void leftRotate(int arr[], int d, int n) 
 {
 for (int i = 0; i < d; i++) 
 leftRotatebyOne(arr, n);
 }
 static void leftRotatebyOne(int arr[], int n) 
 {
 int i, temp;
 temp = arr[0];
 for (i = 0; i < n - 1; i++) 
 arr[i] = arr[i + 1];
 arr[n - 1] = temp;
 }

static void printArray(int arr[], int n) 
 {
 for (int i = 0; i < n; i++) 
 System.out.print(arr[i] + " ");
 }

public static void main(String[] args)
 {

int arr[] = {1, 2, 3, 4, 5};
 leftRotate(arr, 2, 5);
 printArray(arr, 5);
 }
 } 

