import java.util.*;

public class Main 
 {

public static void shift(int[] arr)
 {
 int j = 0;
 for (int i = 0; i < arr.length; i++)
 {
 if (arr[i] < 0) 
 {
 if (i != j) 
 swap(arr, i, j);
 j++;
 }
 }
 for (int i = 0; i < arr.length; i++) 
 {
 System.out.print(arr[i] + " ");
 }
 }

public static void swap(int[] arr, int i, int j)
 {
 int temp = arr[i];
 arr[i] = arr[j];
 arr[j] = temp;
 }

public static void main(String[] args) throws Exception
 {

int[] arr = new int[]{ -1,0,3,1,-4}; 

shift(arr);
 }
 } 

import java.util.*;

public class Main 
 {

public static void Union(int[] arr1, int[] arr2) 
 {
 int i = 0;
 int j = 0;

while (i < arr1.length && j < arr2.length)
 {
 if (arr1[i] < arr2[j]) 
 {
 System.out.print(arr1[i] + " ");
 i++;
 }
 else if (arr2[j] < arr1[i]) 
 {
 System.out.print(arr2[j] + " ");
 j++;
 } else 
 {
 System.out.print(arr1[i] + " ");

i++;
 j++;
 }

}
 while (i < arr1.length) 
 {
 System.out.print(arr1[i] + " ");
 i++;
 }
 while (j < arr2.length) 
 {
 System.out.print(arr2[j] + " ");
 j++;
 }
 }

public static void Intersection(int[] arr1, int[] arr2) 
 {
 int i = 0;
 int j = 0;
 while (i < arr1.length && j < arr2.length) 
 {
 if (arr1[i] < arr2[j]) 
 {
 i++;
 } 
 else if (arr1[i] > arr2[j])
 {
 j++;
 } else
 {
 System.out.print(arr1[i] + " ");
 i++;
 j++;
 }
 }
 }

public static void main(String[] args) throws Exception
 {
 Scanner scn = new Scanner(System.in);

int n1 = 3;
 System.out.println("The size of the array 1:- "+n1);
 int[] arr1 = new int[]{ 1,2,3}; 
 System.out.println("The Elements of array 1");
 for (int i = 0; i < n1; i++) 
 {
 System.out.print(arr1[i] + " ");
 }
 System.out.println();

int n2 = 4;
 System.out.println("The size of the array 2:- "+n2);

int[] arr2 = new int[]{4,5,6,7}; 
 System.out.println("The Elements of array 2");

for (int i = 0; i < n2; i++) 
 {
 System.out.print(arr2[i] + " ");
 }
 System.out.println();
 System.out.println("The Union of the two array is ");
 Union(arr1, arr2);
 System.out.println();
 Intersection(arr1, arr2);
 }
 } 

