import java.util.*;

public class Main 
 {

public static void main(String[] args)
 {
 int[] arr = { 7, 4, 8, 8, 8, 9 };
 int k = 6;
 int ans = getMinDiff(arr, arr.length, k);
 System.out.println(ans);
 }

public static int getMinDiff(int[] arr, int n, int k)
 {

Arrays.sort(arr);
 int ans = (arr[n - 1] + k)- (arr[0] + k);
 int tempmax = arr[n - 1] - k; 
 int tempmin = arr[0] + k; 
 int max, min;

for (int i = 0; i < n - 1; i++) 
 {
 if (tempmax > (arr[i] + k))
 {
 max = tempmax;
 }
 else {
 max = arr[i] + k;
 }

if (tempmin < (arr[i + 1] - k)) 
 {
 min = tempmin;
 }
 else 
 {
 min = arr[i + 1] - k;
 }

if (ans > (max - min))
 {
 ans = max - min;
 }
 }
 return ans;
 }
 } 

public class Main 
 {

private static int minJumps(int[] arr, int n) 
 {

int jumps[] = new int[n];

int i, j;
 if (n == 0 || arr[0] == 0) return Integer.MAX_VALUE;

jumps[0] = 0;

for (i = 1; i < n; i++) 
 {
 jumps[i] = Integer.MAX_VALUE;

for (j = 0; j < i; j++) 
 {
 if (i <= j + arr[j] && jumps[j] != Integer.MAX_VALUE) 
 {
 jumps[i] = Math.min(jumps[i], jumps[j] + 1);
 break;
 }
 }
 }
 return jumps[n - 1];
 }

public static void main(String[] args) 
 {
 int arr[] = {2, 1, 3, 2, 3, 4, 5, 1, 2, 8};

System.out.println"Minimum number of jumps to reach end is : " + minJumps(arr, arr.length));
 }
 } 

import java.util.*;

public class Main 
 {
 static int findduplicate(int[] arr, int n) 
 {

if (n <= 1) return -1;

int slow = arr[0];
 int fast = arr[arr[0]];

while (fast != slow) 
 {

slow = arr[slow];

fast = arr[arr[fast]];
 }

fast = 0;
 while (slow != fast) 
 {
 slow = arr[slow];
 fast = arr[fast];
 }
 return slow;
 }

public static void main(String args[]) 
 {
 int[] arr = {1, 2, 3, 4, 5, 6, 3};
 int n = arr.length;
 System.out.print(findduplicate(arr, n));
 }
 } 

import java.util.*;

public class Main
 {
 static int arr1[] = new int[]{1, 12, 9, 3, 17, 20};
 static int arr2[] = new int[]{2, 3, 8, 13};
 static void merge(int m, int n) 
 {
 for (int i = n - 1; i >= 0; i-\-)
 {
 int j, last = arr1[m - 1];
 for (j = m - 2; j >= 0 && arr1[j] > arr2[i]; j-\-)
 arr1[j + 1] = arr1[j];

if (j != m - 2 || last > arr2[i]) 
 {
 arr1[j + 1] = arr2[i];
 arr2[i] = last;
 }
 }
 }

public static void main(String[] args) 
 {
 merge(arr1.length, arr2.length);
 System.out.print("After Merging First Array: ");
 System.out.println(Arrays.toString(arr1));
 System.out.print("Second Array: ");
 System.out.println(Arrays.toString(arr2));
 }
 }

public class Main 
 {

public static void main(String[] args)
 {
 int[] a = {-2, -3, 4, -1, -3};
 System.out.println("Maximum contiguous sum is " + maxSubArraySum(a));
 }
 static int maxSubArraySum(int a[]) 
 {
 int size = a.length;
 int max_so_far = Integer.MIN_VALUE, max_ending_here = 0;
 for (int i = 0; i < size; i++) 
 {
 max_ending_here = max_ending_here + a[i];
 if (max_so_far < max_ending_here) 
 max_so_far = max_ending_here;
 if (max_ending_here < 0) 
 max_ending_here = 0;
 }
 return max_so_far;
 }
 }

import java.util.*;
 public class Main 
 {

public static void mergeIntervals(Interval arr[])
 {

if (arr.length <= 0)
 return;
 Stack<Interval> stack=new Stack<>();

Arrays.sort(arr,new Comparator<Interval>()
 {
 public int compare(Interval i1,Interval i2)
 {
 return i1.start-i2.start;
 }
 });

stack.push(arr[0]);
 for (int i = 1 ; i < arr.length; i++)
 {
 Interval top = stack.peek();
 if (top.end < arr[i].start)
 stack.push(arr[i]);

else if (top.end < arr[i].end)
 {
 top.end = arr[i].end;
 stack.pop();
 stack.push(top);
 }
 }

System.out.print("The Merged Intervals are: ");
 while (!stack.isEmpty())
 {
 Interval t = stack.pop();
 System.out.print("["+t.start+","+t.end+"] ");
 }
 }

public static void main(String args[])
 {
 Interval arr[]=new Interval[4];
 arr[0]=new Interval(6,8);
 arr[1]=new Interval(1,9);
 arr[2]=new Interval(2,4);
 arr[3]=new Interval(4,7);
 mergeIntervals(arr);
 }
 }

class Interval
 {
 int start,end;
 Interval(int start, int end)
 {
 this.start=start;
 this.end=end;
 }
 } 

