class Main
 {

public static void main(String args[])
 {
 int a[] = {11, 9, 7, 15, 6, 10, 5, 17};

System.out.println("Array Before Insertion Sort: ");
 printArray(a);

insertionSort(a);

System.out.println("Array After Insertion Sort: ");
 printArray(a);
 }

/*Function to sort array using insertion sort*/
 static void insertionSort(int arr[])
 {
 int len = arr.length; 
 for (int i = 1; i < len; i++) 
 { 
 int key = arr[i]; 
 int j = i - 1; 

/* Shift elements of a[i-1 .... 0], that are greater than key, to one position right of their current position */ 
 while (j >= 0 && arr[j] > key)
 {
 arr[j + 1] = arr[j];
 j = j - 1;
 }
 arr[j + 1] = key;
 }
 }

/* A utility function to print array of size n*/
 static void printArray(int a[])
 {
 int len = a.length;
 for (int i = 0; i < len; ++i)
 System.out.print(a[i] + " ");
 System.out.println();
 }
 } 

// Time Complexity : O(N^2)
 // Space Complexity : O(1)
 // Best Case : When already Sorted, Worst Case : When reverse Sorted

class Main
 {
 // Main method, responsible for the execution of the code
 public static void main(String args[])
 {
 int arr[] = {72, 50, 10, 44, 20};

selectionSort(arr);

System.out.println("Sorted array");

printArray(arr);
 }

static void selectionSort(int a[])
 {
 int len = a.length;

// One by one move boundary of unsorted sub-array
 for (int i = 0; i < len-1; i++)
 {
 // Find the minimum element in unsorted array
 int min = i;
 for (int j = i+1; j < len; j++)
 if (a[j] < a[min])
 min = j;

// Swap the found minimum element with the
 // first element in unsorted part of the array
 int temp = a[min];
 a[min] = a[i];
 a[i] = temp;
 }
 }

// Prints the sorted array
 static void printArray(int a[])
 {
 int len = a.length;
 for (int i=0; i<len; ++i)
 System.out.print(a[i] + " ");

System.out.println();
 }
 } 

// Time Complexity : O(N^2)
 // Space Complexity : O(1)
 // Best Case, Worst Case, Avg Case : O(n^2)

class Main
 {
 public static void display(int[] arr, int size)
 {
 for(int i = 0; i < size; i++) 
 {
 System.out.print(arr[i] + " ");
 }
 System.out.println();
 }
 public static void main(String[] args)
 {
 int[] a = {12, 8, 4, 14, 36, 64, 15, 72, 67, 84};

int size = a.length;
 display(a, size);

mergeSort(a, 0, size - 1);
 display(a, size);
 }

// this function apply merging and sorting in the array
 static void mergeSort(int[] a, int left, int right)
 {
 int mid;
 if(left < right)
 {

mid = (left + right) / 2;

mergeSort(a, left, mid);
 mergeSort(a, mid + 1, right);
 merge(a, left, mid, right);
 }
 }

static void merge(int[] arr, int left, int mid, int right)
 {
 int i, j, k;
 int n1 = mid - left + 1;
 int n2 = right - mid;

// create temp arrays to store left and right sub-arrays
 int L[] = new int[n1];
 int R[] = new int[n2];

// Copying data to temp arrays L[] and R[]
 for (i = 0; i < n1; i++)
 L[i] = arr[left + i];
 for (j = 0; j < n2; j++)
 R[j] = arr[mid + 1 + j];

// here we merge the temp arrays back into arr[l..r]
 i = 0; // Starting index of L[i]
 j = 0; // Starting index of R[i]
 k = left; // Starting index of merged sub-array

while (i < n1 && j < n2)
 {
 // place the smaller item at arr[k] pos
 if (L[i] <= R[j]) 
 {
 arr[k] = L[i];
 i++;
 }
 else {
 arr[k] = R[j];
 j++;
 }
 k++;
 }
 // Copy the remaining elements of L[], if any
 while (i < n1) {
 arr[k] = L[i];
 i++;
 k++;
 }
 // Copy the remaining elements of R[], if any
 while (j < n2) 
 {
 arr[k] = R[j];
 j++;
 k++;
 }
 }
 }

class Main
 {
 // this function display the array
 public static void display(int[] arr, int size)
 {
 for(int i = 0; i < size; i++) 
 {
 System.out.print(arr[i] + " ");
 }
 System.out.println();
 }
 // main function of the program
 public static void main(String[] args)
 {
 int[] a = {12, 11, 13, 5, 6, 7 };

int size = a.length;
 display(a, size);

quickSort(a, 0, size - 1);
 display(a, size);
 }
 // A utility function to swap two elements
 static void swap(int[] arr, int i, int j)
 {
 int temp = arr[i];
 arr[i] = arr[j];
 arr[j] = temp;
 }
 //Recursive function to apply quickSort
 static void quickSort(int[] arr, int low, int high)
 {
 if (low < high)
 {
 /* indexPI is partitioning index, partition() function will return index of partition */
 int indexPI = partition(arr, low, high);

quickSort(arr, low, indexPI - 1); //left partition
 quickSort(arr, indexPI + 1, high); //right partition
 }
 }
 /* Partition function to do Partition elements on the left side of pivot elements would be smaller than pivot elements on the right side of pivot would be greater than the pivot */
 static int partition(int[] arr, int low, int high)
 {
 // Pivot element selected as right most element in array each time.
 int pivot = arr[high];
 int swapIndex = (low - 1); //swapping index.

for (int j = low; j <= high- 1; j++)
 {

if (arr[j] < pivot)
 {
 swapIndex++; //increment swapping index.
 swap(arr, swapIndex, j);
 }
 }

swap(arr, swapIndex + 1, high);

return (swapIndex + 1);
 }
 }

import java.util.util;

class countSort
 {
 void applycountSort(int array[], int size)
 {
 int[] output = new int[size + 1];

// Here we find the largest item in the array
 int max = array[0];
 for (int i = 1; i < size; i++) 
 {
 if (array[i] > max)
 max = array[i];
 }
 int[] count = new int[max + 1];

// Initialize the count for each element in array to 0
 for (int i = 0; i < max; ++i) 
 {
 count[i] = 0;
 }

// For each element we store the count
 for (int i = 0; i < size; i++)
 {
 count[array[i]]++;
 }

// Store the cummulative count of each array
 for (int i = 1; i <= max; i++)
 {
 count[i] += count[i - 1];
 }
// Search the index of each element of the actual array in count array, and
 // place the elements in output array
 for (int i = size - 1; i >= 0; i-\-) 
 {
 output[count[array[i]] - 1] = array[i];
 count[array[i]]-\-;
 }

// Transfer the sorted items into actual array
 for (int i = 0; i < size; i++) 
 {
 array[i] = output[i];
 }
 }

// Driver code
 public static void main(String args[]) 
 {
 int[] data = {2, 5, 2, 8, 1, 4, 1};
 int size = data.length;

countSort obj = new countSort();
 obj.applycountSort(data, size);

System.out.println("Array After Sorting: ");
 System.out.println(Arrays.toString(data));
 }
 }

import java.util.*;

class Main
 {
 //Main Method to check for above function
 public static void main (String[] args)
 {
 int a[] = {17, 45, 75, 90, 82, 24, 12, 60};
 int len = a.length;
 radixsort(a,len);
 print(a,len);
 }

// A utility function to get maximum value in a[]
 static int getMax(int a[], int n)
 {
 int mx = a[0];
 for (int i = 1; i < len; i++)
 if (a[i] > mx)
 mx = a[i];
 return mx;
 }

// A function to do counting sort of arr[] according to
 // the digit represented by exp.
 static void countSort(int a[], int len, int exp)
 {
 int output[] = new int[len]; // output array
 int i;
 int count[] = new int[10];
 Arrays.fill(count,0);

// Store count of occurrences in count[]
 for (i = 0; i < len; i++)
 count[ (a[i]/exp)%10 ]++;

// Change count[i] so that count[i] now contains
 // actual position of this digit in output[]
 for (i = 1; i < 10; i++)
 count[i] += count[i - 1];

// Build the output array
 for (i = len - 1; i >= 0; i-\-)
 {
 output[count[ (a[i]/exp)%10 ] - 1] = a[i];
 count[ (a[i]/exp)%10 ]-\-;
 }

// Copy the output array to arr[], so that arr[] now
 // contains sorted numbers according to curent digit
 for (i = 0; i < len; i++)
 a[i] = output[i];
 }

// The main function to that sorts arr[] of size n using
 // Radix Sort
 static void radixsort(int a[], int len)
 {
 // Find the maximum number to know number of digits
 int m = getMax(a, len);

// Do counting sort for every digit. Note that instead
 // of passing digit number, exp is passed. exp is 10^i
 // where i is current digit number
 for (int exp = 1; m/exp > 0; exp *= 10)
 countSort(a, len, exp);
 }

// A utility function to print an array
 static void print(int a[], int len)
 {
 for (int i=0; i<len; i++)
 System.out.print(a[i]+" ");
 }

}

public class Main
 { 

public static void main(String args[]) 
 { 
 int a[] = {12, 11, 13, 5, 6, 7}; 
 int len = a.length;

PrepInsta ob = new PrepInsta(); 
 ob.sort(a); 

System.out.println("Sorted array is"); 
 printArray(a); 
 } 
 public void sort(int a[]) 
 { 
 int len = a.length; 

// Build heap (rearrange array) 
 for (int i = len / 2 - 1; i >= 0; i-\-) 
 heapify(a, len, i); 

// One by one extract an element from heap 
 for (int i=len-1; i>=0; i-\-) 
 { 
 // Move current root to end 
 int temp = a[0]; 
 a[0] = a[i]; 
 a[i] = temp; 

// call max heapify on the reduced heap 
 heapify(a, i, 0); 
 } 
 } 

// To heapify a subtree rooted with node i which is 
 // an index in arr[]. n is size of heap 
 void heapify(int a[], int len, int i) 
 { 
 int largest = i; // Initialize largest as root 
 int l = 2*i + 1; // left = 2*i + 1 
 int r = 2*i + 2; // right = 2*i + 2 

// If left child is larger than root 
 if (l < len && a[l] > a[largest])
 largest = l;

// If right child is larger than largest so far
 if (r < len && a[r] > a[largest])
 largest = r;

// If largest is not root
 if (largest != i)
 {
 int swap = a[i];
 a[i] = a[largest];
 a[largest] = swap;

// Recursively heapify the affected sub-tree
 heapify(a, len, largest);
 }
 }

/* A utility function to print array of size n */
 static void printArray(int a[])
 {
 int len = a.length;
 for (int i=0; i<len; ++i)
 System.out.print(a[i]+" ");
 System.out.println();
 }

}

A linked list is a linear data structure. In which we can sequentially store the data. Unlike an array linked list is a dynamic data structure the size of a linked list can grow or shrink depending on the situation.
